def kMers(text, k):         # vraca kmere, nama ode trebaju samo 3-meri
  kmers=[]
  for i in range(0, len(text), k):
    kmers.append(text[i:i+k])
  return kmers

first="ACGU"
second="ACGU"
third="UCAG"
letters="NNKKTTTTSSRRIIIMHHQQPPPPRRRRLLLLDDEEAAAAGGGGVVVVYY**SSSSCC*WFFLL"   # length(letters)=64
def Translate(abc):                 # gledamo po onemu krugu i somo za svako slovo dodojemo onoliko mista koliko ih zauzima i tako dodemo do trozeneg slova
  for i in range(0, 4):
    if (first[i]==abc[0]):
      br=i*16
  for i in range(0, 4):
    if (second[i]==abc[1]):
      br+=i*4
  for i in range(0, 4):
    if (third[i]==abc[2]):
      br+=i
  if (letters[br]=="*"):
    return ""
  else:
    return letters[br]

def PTP(rna):
  kmers=kMers(rna, 3)
  peptide=""
  for i in range(0, len(kmers)):
    peptide+=Translate(kmers[i])
  print(peptide)

PTP("")
